mapper.select:
SELECT PROGRAM_CODE, PROGRAM_NAME, START_DATE, END_DATE, CAPACITY, USE_YN, DESCRIPTION, REG_ID, REG_DT, UPD_ID, UPD_DT FROM ADM_PROGRAM WHERE 1=1
AND PROGRAM_CODE LIKE CONCAT('%', #{searchKeyword}, '%')
AND PROGRAM_NAME LIKE CONCAT('%', #{searchKeyword}, '%')
AND USE_YN = #{searchKeyword}
ORDER BY REG_DT DESC, PROGRAM_CODE DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.insert:
INSERT INTO ADM_PROGRAM (
      PROGRAM_CODE
    , PROGRAM_NAME
    , START_DATE
    , END_DATE
    , CAPACITY
    , USE_YN
    , DESCRIPTION
    , REG_ID
    , REG_DT
    , UPD_ID
    , UPD_DT
) VALUES (
      #{programCode}
    , #{programName}
    , #{startDate}
    , #{endDate}
    , #{capacity}
    , #{useYn}
    , #{description}
    , #{regId}
    , NOW()
    , #{updId}
    , NOW()
)

mapper.update:
UPDATE ADM_PROGRAM
   SET PROGRAM_NAME = #{programName}
     , START_DATE = #{startDate}
     , END_DATE = #{endDate}
     , CAPACITY = #{capacity}
     , USE_YN = #{useYn}
     , DESCRIPTION = #{description}
     , UPD_ID = #{updId}
     , UPD_DT = NOW()
 WHERE PROGRAM_CODE = #{programCode}
